package JavaMachineLearningClass;

public class PI {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = 3.14;
		
		double radius = 10.0;
		double circleArea = radius*radius*PI;
		
		//원의 면적을 화면에 출력한다.
		System.out.println("원의 면적 = "+circleArea);

	}

}
