package JavaProgrammingClass;

public class Java2_3_증감연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int d =3;
		int a =3;
		
		a = d++;
		System.out.println("a="+a+" d="+d);
		a = ++d;
		System.out.println("a="+a+" d="+d);
		a = d--;
		System.out.println("a="+a+" d="+d);
		a = --d;
		System.out.println("a="+a+" d="+d);
		
		
		//시프트 <<왼쪽 *2 
		//시프트 >>오른쪽 /2 가됨.
		//시프트 
	}

}
