package JavaProgrammingClass;

public class Java3_1_구구단실습2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for( int i =1;i<10;i++) {
			for(int j =1;j<10;j++) {
				System.out.print(i+"*"+j+"="+(j*i));
				System.out.print("\t");
				}
			System.out.println();
			}	
		}
	}
